insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.2.1.5', 'cae', '1.2.1.5', 'Dterminer les secteurs gographiques prioritaires de dveloppement des nergies renouvelables et de rcupration', '<p>Les secteurs de dveloppement dtermins permettent :</p>
<ul>
<li>
<p>une utilisation majoritaire des rejets thermiques disponibles et des nergies renouvelables produites localement, avec indication des consquences (notamment sur la qualit de l''air, en lien avec l''analyse des impacts du changement climatique,  la fois sur la production, hydraulique par exemple, et sur les besoins, en t par exemple),</p>
</li>
<li>
<p>le dveloppement de rseaux de chaleur, de froid, etc, en distinguant les zones propices aux crations de nouveaux rseaux et/ou les zones favorables  la densification et aux extensions (densit nergtique prconise suprieure  1,5 MWh/ml, source : ADEME).</p>
</li>
</ul>
', '', '', '', '', '', null, 20.0, 'mise en uvre');
