insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.2.1.6.3', 'cae', '1.2.1.6.3', 'Intgrer des mesures de matrise de la demande en nergie', '<p>Intgrer des mesures de matrise de la demande en nergie afin de dimensionner et localiser les installations de faon cohrente vis--vis des btiments ayant vocation  devenir moins consommateurs dnergie quaujourdhui.</p>
