insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.2.1.7.1', 'cae', '1.2.1.7.1', 'Mettre  disposition de lensemble des parties prenantes du territoire la programmation nergtique', '<p>La programmation nergtique mise  disposition comprend tudes de potentiel, de faisabilit et de dveloppement des ENR pour une appropriation et dclinaison des objectifs  une chelle infrieure (commune, entreprise, particulier, etc.)</p>
