insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.2.3.2.22', 'cae', '1.2.3.2.22', 'Prendre en compte l''objectif national de recycler 70% des dchets du BTP et mettre en uvre des actions pour y contribuer', '<p>A minima pour les chantiers publics (taux de recyclage suprieur ou gal  70 %) et de manire plus large, participation de la collectivit  la coordination/mise en place de la filire prive de collecte et de valorisation des dchets du BTP (identification de sites propices, facturation d''un service de reprise de dchets dans les dchetteries publiques selon les capacits d''accueil, contrat entre collectivits et distributeurs...).</p>
