insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.3.1.2.1', 'cae', '1.3.1.2.1', 'Raliser des analyses croises des enjeux climat-air-nergie et urbains', '<p>Ralisation d''analyses croises des enjeux climat-air-nergie et urbains, de type AEU2 ou utilisation d''outils d''aide  la dcision comme GES-Urbanisme (CEREMA) ou d''outils de modlisation de l''impact de la politique d''urbanisme sur la qualit de l''air pour l''laboration des documents d''urbanisme (PLU, SCOT) et sur les secteurs  enjeux pour les traduire en orientations d''amnagement opposables</p>
