insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.3.1.2.3', 'cae', '1.3.1.2.3', 'Intgrer dans le rapport de prsentation du PLU/SCOT un tat des lieux de la situation en matire dmissions de gaz  effet de serre, de consommations et productions nergtiques, dmissions de polluants atmosphriques, des vulnrabilits climatiques', '<p>En complment des thmatiques rglementaires (habitat, dplacement, etc.) et en lien troit avec les donnes disponibles dans les autres documents de planification (notamment PDM, PLH, PCAET), le rapport de prsentation du PLU/SCOT comporte un tat des lieux de la situation en matire dmissions de gaz  effet de serre, de consommations et productions nergtiques, dmissions de polluants atmosphriques, des vulnrabilits climatiques (exemple : tat des lieux des quartiers les plus touchs par la chaleur estivale)</p>
', '', '', '', '', '', null, null, null);
