insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.3.1.3.1', 'cae', '1.3.1.3.1', 'Intgrer dans le PADD des engagements clairs pour l''adaptation et l''attnuation par rapport au changement climatique', '<p>Le plan d''amnagement et de dveloppement durable (PADD) comporte des engagements clairs pour l''adaptation et l''attnuation par rapport au changement climatique (ces engagements sont si possible spatialiss et chiffrs).</p>
<p>Par exemple en matire de rseaux dnergie pour favoriser le dveloppement des rseaux de chaleur aliments majoritairement par des nergies renouvelables et de rcupration (ENRR) et pour coordonner les diffrents rseaux d''nergies, ou en matire de trame verte et bleue pour optimiser globalement la rsistance et la rsilience vis--vis des vnements climatiques extrmes).</p>
', '', '', '', '', '', null, null, null);
