insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.3.1.4.5', 'cae', '1.3.1.4.5', 'Contribuer explicitement dans les  rglement du PLU et le DOO du SCOT (en dictant certaines rgles au PLU le cas chant)  la lutte et  l''adaptation au changement climatique, ainsi qu'' la prservation de la qualit de l''air', '<p>En favorisant notamment : la compacit des formes urbaines, les conomies d''nergie et la diversification des sources d''nergie, la vgtalisation et la gestion locale des eaux pluviales, la limitation de la voiture, la mixit fonctionnelle.</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.3.1.4.6', 'cae', '1.3.1.4.6', 'Utiliser les nouvelles possibilits Grenelle, ALUR et TECV', '<p>Exigences nergtiques et environnementales renforces dans certains secteurs du PLU/SCOT, bonification de gabarit sur critres de performance nergtique, densit minimale de construction dans les secteurs bien desservis pas les TC,  proximit des rseaux de chaleur alimentes majoritairement par des ENRR et/ou  proximit d''quipements collectifs aux besoins importants d''nergie, coefficient de biotope par surface, normes maximales de stationnement motoriss, etc.</p>
', '', '', '', '', '', null, null, null);
