insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.3.1.5.4', 'cae', '1.3.1.5.4', 'Limiter lartificialisation des sols sur le long terme, avec la mise en place de dispositifs rglementaires de protection et doutils d''intervention foncire', '<p>Outils d''intervention foncire : droit de premption de la SAFER, droit de premption urbain des communes/EPCI, PAEN, zones agricoles protges, ENS, zone damnagement diffr, zone agricole protge.</p>
