insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.3.2.3.1', 'cae', '1.3.2.3.1', 'Dfinir des prconisations  lchelle du quartier ou de la ville inspires notamment des outils nationaux existants (cahiers thmatiques AEU2, label national Ecoquartier, HQE-Performance quartier)', '<p>Compacit des formes urbaines, orientation et implantation des btiments les uns par rapport aux autres, raccordement aux rseaux de chaleur aliments majoritairement par des ENRR, autosuffisance nergtique, quartier sans voiture, vgtalisation/dminralisation des espaces (en limitant les allergnes potentiels), gestion de leau et rafrachissement urbain, concertation citoyenne, etc.</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.3.2.3.2', 'cae', '1.3.2.3.2', 'Dfinir des critres  lchelle du btiment : BEPos, label E+/C-, Maison passive, BBC, Effinergie, Ecodom, HQE avec objectifs nergie ou air dfinis, label btiment biosourc, chauffage trs performant (flamme verte 7 toiles pour le bois ou quivalent) ou valorisation des rejets thermiques, confort d''t, qualit de l''air intrieur...', '', '', '', '', '', '', null, null, null);
