insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.3.2.5.2', 'cae', '1.3.2.5.2', 'Utiliser des spcifications techniques pour fixer des objectifs de performances environnementales, de manire prescriptive,  lchelle de la ville, du quartier et du btiment.', '<p>A l''chelle du quartier : AEU2, HQE Performance Quartier, etc.</p>
