insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_5.2.1.3', 'cae', '5.2.1.3', 'Raliser chaque anne une valuation climat du budget', '<p>La collectivit mne chaque anne une valuation globale de son budget sous langle climat en dterminant les actions en faveur de la neutralit carbone et celles qui contribuent  mettre des gaz  effet de serre, en suivant la mthodologie propose par I4CE par exemple. Les rsultats de cette valuation climat du budget sont prsents aux lu.e.s en amont des discussions budgtaires et du vote du budget.</p>
', '', '', '', '', '', null, 15.0, 'mise en uvre');
