insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_5.2.1.5.1', 'cae', '5.2.1.5.1', 'La collectivit tudie lensemble des sources de financement possibles pour les actions de sa politique climat-aire-nergie.', '<p>En particulier, la collectivit suit les dispositifs ddis mis  disposition par lEtat franais et ses oprateurs (ADEME, ANCT, PIA, Banque des Territoires), ainsi que les fonds europens (FEDER, LEADER, FSE, H2020, UIA, INTERREG, Life, Life IP, CEF, ...).</p>
', '', '', '', '', '', null, null, null);
