insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_5.2.1.7.1', 'cae', '5.2.1.7.1', 'Apporter du soutien financier  des thmatiques environnementales varies,  des cibles varies et sous diffrentes formes', '<p>Thmatiques environnementales : btiments, nergie, air, eau, agriculture, ENR, mobilit...</p>
<p>Cibles : particuliers, association, entreprises, agriculteurs...</p>
<p>Formes du soutien financier : partenariat avec des banques, tiers-investissement, fonds de garantie, fonds d''investissement, prise de participation dans des SCIC/SEM uvrant pour le DD ou des SAS/SA portant des projets de production dnergie renouvelable sur leur territoire...</p>
', '', '', '', '', '', null, null, null);
