insert into indicateur_definition(id, indicateur_group, identifiant, valeur_indicateur, nom, description, unite, obligation_eci, parent) values ('cae_35', 'cae', '35', null, 'Part modale piton', '<p>La part modale est une part modale en nombre de dplacements.</p>
<p>Les valeurs limites et cibles (selon le nombre d''habitants, limite de 15-25%, cible de 25-35%) sont donnes  titre indicatif pour le conseiller, qui doit galement juger de l''volution de la part modale au fil du temps et des caractristiques du territoire (ville centre dense favorisant la marche ou territoire tendu d''une agglomration...). A dfaut de possder les parts modales issues d''une enqute mnages, les collectivits peuvent utiliser les donnes INSEE donnant les parts modales des dplacements domicile-travail pour la population active (tableau NAV2A ou NAV2B).</p>
', 'Non trouv', false, null);insert into indicateur_action(indicateur_id, action_id) values ('cae_35', 'cae_4.1.1');insert into indicateur_action(indicateur_id, action_id) values ('cae_35', 'cae_4.3.1');
insert into indicateur_definition(id, indicateur_group, identifiant, valeur_indicateur, nom, description, unite, obligation_eci, parent) values ('cae_64', 'cae', '64', null, 'Part d''tablissements scolaires couverts par un PDES ou un pdibus/vlobus', '<p>L''indicateur comptabilise le nombre d''tablissement scolaires (coles primaires, collges, lyces) couverts par un Plan de Dplacements Etablissements Scolaires ou un pdibus/vlobus (pour les coles primaires principalement) sur le territoire et le rapporte au nombre total d''tablissements scolaires. Ce chiffre doit tre en augmentation chaque anne. Des valeurs indicatives limites (10%) et cibles (30%) sont donnes, bases sur des donnes ADEME et les meilleurs scores des collectivits Cit''ergie.</p>
', 'Non trouv', false, null);insert into indicateur_action(indicateur_id, action_id) values ('cae_64', 'cae_6.5.3');
insert into indicateur_definition(id, indicateur_group, identifiant, valeur_indicateur, nom, description, unite, obligation_eci, parent) values ('cae_25.a', 'cae', '25.a', null, 'Production d''lectricit renouvelable  (MWh)', '<p>Cet indicateur mesure la production d''lectricit renouvelable sur le territoire (initiative publique et prive). Les nergies renouvelables prise en compte sont celles cites selon les filires cites dans le Dcret n 2016-849 du 28 juin 2016  relatif au plan climat-air-nergie territorial :  olien terrestre, solaire PV, solaire thermodynamique, hydraulique, biomasse solide, biogaz, gothermie.</p>
', 'MWh', false, null);insert into indicateur_action(indicateur_id, action_id) values ('cae_25.a', 'cae_3.2.3');
insert into indicateur_definition(id, indicateur_group, identifiant, valeur_indicateur, nom, description, unite, obligation_eci, parent) values ('cae_25.b', 'cae', '25.b', null, 'Taux de production d''lectricit renouvelable sur le territoire (%)', '<p>Cet indicateur mesure la production d''lectricit renouvelable sur le territoire, par la collectivit, ses partenaires et les particuliers, rapport  la consommation totale d''lectricit du territoire (nergie finale). Les nergies renouvelables considres sont celles cites dans le dcret Dcret n 2016-849 du 28 juin 2016  relatif au plan climat-air-nergie territorial  (olien  terrestre,  solaire  photovoltaque,  solaire  thermodynamique,  hydraulique,  biomasse  solide, biogaz,  gothermie). L''lectricit produite par cognration via incinration des dchets en mlange compte pour 50% comme une nergie renouvelable (biomasse solide). La cognration  partir d''nergie fossile n''est pas prise en compte.</p>
<p>La collectivit doit avoir effectu un bilan de ses consommations et productions d''ENR tel que dcrit  l''action 1.1.2:</p>
<p>Raliser le diagnostic Climat-Air-Energie du territoire</p>
<ul>
<li></li>
</ul>
<ul>
<li>
<p>Valeur cible pour les territoires sans potentiel olien et hydraulique : 16%</p>
</li>
<li>
<p>Valeur cible pour les territoires  fort potentiel : 40%</p>
</li>
<li>
<p>Valeur cible pour les DOM : 75%</p>
</li>
</ul>
', '%', false, null);insert into indicateur_action(indicateur_id, action_id) values ('cae_25.b', 'cae_3.2.3');
insert into indicateur_definition(id, indicateur_group, identifiant, valeur_indicateur, nom, description, unite, obligation_eci, parent) values ('cae_11', 'cae', '11', null, 'Surface annuelle artificialise (ha/an)', '<p>Lindicateur mesure les surfaces artificialises chaque anne a minima par lhabitat et les activits, et dans la mesure du possible galement pour les autres motifs (infrastructures routires, etc.). Si lindicateur nest pas disponible annuellement, il sagit de la moyenne annuelle sur une priode plus large, tabli  loccasion de llaboration ou de la rvision du PLU ou du SCOT (valuation rglementaire de la consommation d''espaces naturels, agricoles et forestiers).</p>
', 'ha/an', false, null);insert into indicateur_action(indicateur_id, action_id) values ('cae_11', 'cae_1.3.1');
insert into indicateur_definition(id, indicateur_group, identifiant, valeur_indicateur, nom, description, unite, obligation_eci, parent) values ('cae_40.a', 'cae', '40.a', null, 'Consommation annuelle d''nergie des vhicules (VP) de la collectivit (kWh/an.employ)', '<p>L''indicateur mesure la consommation d''nergie en kWh (gazole, essence, GPL, GNV, lectricit, biogaz, agro-carburants...) des vhicules de type &quot;vhicule particulier&quot; pour le fonctionnement de la collectivit, divis par le nombre d''agents et/ou par kilomtre effectu. Facteurs de conversion simplifis : gazole et essence 10 kWh/L, GPL 7 kWh/L, GNV 11 kWh/m3.</p>
', 'VP', false, null);insert into indicateur_action(indicateur_id, action_id) values ('cae_40.a', 'cae_4.1.2');
insert into indicateur_definition(id, indicateur_group, identifiant, valeur_indicateur, nom, description, unite, obligation_eci, parent) values ('cae_40.b', 'cae', '40.b', null, 'Consommation annuelle d''nergie des vhicules (VP) de la collectivit (kWh/an.km)', '<p>dclinaison par kilomtre de l''indicateur 40a:</p>
<p>Consommation annuelle d''nergie des vhicules (VP) de la collectivit (kWh/an.employ)</p>
', 'VP', false, null);insert into indicateur_action(indicateur_id, action_id) values ('cae_40.b', 'cae_4.1.2');