-- Personnalisation regles
INSERT INTO public.personnalisation_regle (action_id, type, formule, description) VALUES ('cae_4.2.1', 'reduction', 'si identite(type, commune) alors max(reponse(voirie_2), 2/8)
sinon si identite(type, EPCI) et reponse(voirie_1, voirie_1_b) alors 0.5
sinon si reponse(voirie_1, voirie_1_c) et reponse(centre_polarite, NON) alors 0.25
', '<p>Pour les communes, le score est r√©duit proportionnelle √† la part dans l‚ÄôEPCI en cas de transfert de la comp√©tence en mati√®re de voirie/stationnement, danms la limite de 2 points pour le pouvoir de police du maire</p>
<p>Pour les intercommunalit√©s qui n‚Äôont la comp√©tence que sur les voiries et parcs de stationnements communautaires, le score est r√©duit de 50 %</p>
<p>En l‚Äôabsence de comp√©tences voirie et stationnement et de zones de polarit√©s le score est r√©duit de 75 %</p>
');
INSERT INTO public.personnalisation_regle (action_id, type, formule, description) VALUES ('cae_4.2.2', 'reduction', 'si reponse(pouvoir_police, NON) ou reponse(voirie_1,voirie_1_b) ou reponse(voirie_1,voirie_1_c) ou reponse(trafic, NON) alors 0.5
sinon si reponse(pouvoir_police, NON) et reponse(trafic, NON) alors 2/16
sinon si reponse(pouvoir_police, NON) et reponse(voirie_1,voirie_1_b) alors 2/16
sinon si reponse(pouvoir_police, NON) et reponse(voirie_1,voirie_1_c) alors 2/16
sinon si reponse(voirie_1,voirie_1_b) et reponse(voirie_1,voirie_1_c) alors 2/16
sinon si reponse(voirie_1,voirie_1_b) et reponse(trafic, NON) alors 2/16
sinon si reponse(voirie_1,voirie_1_c) et reponse(trafic, NON) alors 2/16
', '<p>R√©duction de 50 % pour les collectivit√©s ne disposant pas des comp√©tences en mati√®re de circulation/gestion du trafic (pouvoir de police)</p>
<p>R√©duction de 50 % pour les collectivit√©s ne disposant pas de comp√©tences en mati√®re de voirie (cr√©ation, am√©nagement, entretien) ou qui poss√®dent uniquement les voiries et parcs de stationnements communautaires</p>
<p>R√©duction de 50 % pour s''il n''y a manifestement pas de potentiel d''action ou de probl√®mes li√©s √† la vitesse</p>
<p>R√©duction cumulable, dans la limite de 2 points potentiel restant.</p>
');
INSERT INTO public.personnalisation_regle (action_id, type, formule, description) VALUES ('cae_4.1.1', 'reduction', 'max(reponse(AOM_2), 0.5)
', '<p>Pour une collectivit√© non AOM, le score est proportionnel √† la participation dans la structure AOM dans la limite de 50 %.</p>
');
INSERT INTO public.personnalisation_regle (action_id, type, formule, description) VALUES ('cae_4.1.2', 'reduction', 'si reponse(TC_1, NON) alors 0.8
sinon si reponse(vehiculeCT_1, NON) alors 0.7
sinon si reponse(TC_1, NON) et reponse(vehiculeCT_1, NON) alors 0.5
', '<p>Pour une collectivit√© dont la desserte des locaux par les transports publics est inenvisageable, le score est diminut√© de 20 %.</p>
<p>Pour une collectivit√© ne disposant pas de v√©hicules, le score est diminut√© de 30 %.</p>
<p>Ces 2 r√©ductions sont cumulables.</p>
');
