INSERT INTO public.personnalisation_regle (action_id, type, formule, description) VALUES ('cae_2.3.1', 'desactivation', 'identite(type, EPCI) et reponse(EP_1, EP_1_c)
', '<p>Si la collectivit√© est un EPCI sans comp√©tence sur l''√©clairage public, alors le score est r√©duit √† 0 et les statuts sont &quot;non concern√©s&quot;.</p>
<p>Si la collectivit√© est un EPCI avec une comp√©tence √©clairage public limit√©e, alors la collectivit√© est √©valu√©e sur 2 points (au lieu de 6).</p>
<p>Si la collectivit√© a d√©l√©gu√© sa comp√©tence √©clairage public √† une autre structure, alors le score est proportionnel √† la participation de la collectivit√© dans la structure.</p>
');
INSERT INTO public.personnalisation_regle (action_id, type, formule, description) VALUES ('cae_2.3.3', 'reduction', 'si reponse(voirie_1, voirie_1_b) alors 0.5
sinon si reponse(voirie_1, voirie_1_c) alors 0/2
', '');
INSERT INTO public.personnalisation_regle (action_id, type, formule, description) VALUES ('cae_2.3.3', 'desactivation', 'reponse(voirie_1, voirie_1_c)
', '<p>Si la collectivit√© n''a pas la comp√©tence voirie, alors le score est r√©duit √† 0 et les statuts sont &quot;non concern√©s&quot;.</p>
<p>Si la collectivit√© a une comp√©tence voirie limit√©e, alors la collectivit√© est √©valu√©e sur 1 point (au lieu de 2).</p>
');
INSERT INTO public.personnalisation_regle (action_id, type, formule, description) VALUES ('cae_3.1.1', 'reduction', 'si reponse(AOD_elec, NON) ou reponse(AOD_gaz, NON) alors 7/10
sinon si reponse(AOD_chaleur, NON) alors 6/10
sinon si reponse(AOD_elec, NON) et reponse(AOD_gaz, NON) alors 4/10
sinon si reponse(AOD_elec, NON) et reponse(AOD_chaleur, NON) alors 3/10
sinon si reponse(AOD_gaz, NON) et reponse(AOD_chaleur, NON) alors 3/10
sinon si reponse(AOD_elec, NON) et reponse(AOD_gaz, NON) et reponse(AOD_chaleur, NON) alors 2/10
', '<p>Pour une collectivit√© non autorit√© organisatrice de la distribution d''√©lectricit√©, le score de la 3.1.1 est r√©duit de 30 %.</p>
<p>Pour une collectivit√© non autorit√© organisatrice de la distribution de gaz, le score de la 3.1.1 est r√©duit de 30 %.</p>
<p>Pour une collectivit√© non autorit√© organisatrice de la distribution de chaleur, le score de la 3.1.1 est r√©duit de 40 %.</p>
<p>Ces r√©ductions sont cumulables dans la limite de 2 points restants pour prendre en compte la part d‚Äôinfluence dans les instances comp√©tentes et les actions partenariales.</p>
');
INSERT INTO public.personnalisation_regle (action_id, type, formule, description) VALUES ('cae_3.1.2', 'reduction', 'si reponse(AOD_elec, NON) et reponse(AOD_gaz, NON) et reponse(AOD_chaleur, NON) alors 0.5
