INSERT INTO public.personnalisation_regle (action_id, type, formule, description) VALUES ('cae_3.3.2', 'desactivation', 'reponse(assainissement_1, NON)
', '<p>Pour une collectivit√© sans la comp√©tence assainissement collectif, le score de la 3.3.2 est r√©duit √† 0 point et le statut de la sous-action est &quot;non concern√©e&quot;.</p>
<p>En cas d''absence de potentiel de valorisation √©nerg√©tique (m√©thanisation ou r√©cup√©ration de chaleur) attest√©e par une √©tude portant sur la totalit√© du p√©rim√®tre d‚Äôassainissement, le score de la 3.3.2 est r√©duit √† 50 %.</p>
');
INSERT INTO public.personnalisation_regle (action_id, type, formule, description) VALUES ('cae_3.3.3', 'reduction', 'si identite(type, EPCI) et max(reponse(assainissement_3), 0.5) alors 1.0
