INSERT INTO public.personnalisation_regle (action_id, type, formule, description) VALUES ('cae_1.2.3', 'reduction', 'si reponse(dechets_1, OUI) et reponse(dechets_2, OUI) et reponse(dechets_3, OUI) alors 1.0
